@implementation ATLoginWebViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    [self setupLayoutView];
}

- (void)setupLayoutView {
    self.view.backgroundColor = [UIColor whiteColor];
    UILabel *label = [[UILabel alloc] init];
    label.text = self.titleStr;
    label.textColor = [UIColor blackColor];
    label.textAlignment = NSTextAlignmentCenter;
    label.font = [UIFont systemFontOfSize:20];
    
    UIButton *searchBtn = [[UIButton alloc] init];
    [searchBtn setImage:[UIImage imageNamed:@"btn_navigation_back"] forState:UIControlStateNormal];
    [searchBtn addTarget:self action:@selector(closeVc) forControlEvents:UIControlEventTouchUpInside];
    
    UIView *titleView = [[UIView alloc] init];
    [titleView addSubview:label];
    [titleView addSubview:searchBtn];
    [self.view addSubview:titleView];
    
    [label mas_makeConstraints:^(MASConstraintMaker *make) {
        make.bottom.mas_equalTo(-12);
        make.left.right.mas_equalTo(0);
        make.height.mas_equalTo(20);
    }];
    [searchBtn mas_makeConstraints:^(MASConstraintMaker *make) {
        make.centerY.equalTo(label.mas_centerY).offset(0);
        make.left.mas_equalTo(16);
    }];
    
    [titleView mas_makeConstraints:^(MASConstraintMaker *make) {
        make.top.left.right.mas_equalTo(0);
        make.height.mas_equalTo(kATNavigationBarHeight);
    }];
    
    _textView = [[UITextView alloc] init];
    _textView.font = [UIFont systemFontOfSize:16];
    _textView.textColor = [UIColor colorWithHexString:@"333333"];
    _textView.editable = NO;
    if (_type == 1) {
        _textView.text = privateStr;
    } else if (_type == 2) {
        _textView.text = regisStr;
    } else if (_type == 3) {
        _textView.text = aboutStr;
    }
    [self.view addSubview:_textView];
    
    [_textView mas_makeConstraints:^(MASConstraintMaker *make) {
        make.left.right.bottom.mas_equalTo(0);
        make.top.equalTo(titleView.mas_bottom).offset(0);
    }];
}

- (void)closeVc {
    [self.navigationController popViewControllerAnimated:YES];
}

